mapper.select:
select a.id, u.name as owner, a.name, a.startDate, a.endDate, a.cost, a.description, a.createTime, a.createBy, a.editTime, a.editBy from tbl_activity a join tbl_user u on a.owner = u.id
a.name=#{name}
and u.name=#{owner}
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc limit #{PageCount},#{pageSize}

select Count(*)
from tbl_activity a
join tbl_user u
on a.owner = u.id


        a.name=#{name}


        and  u.name=#{owner}


        and a.startDate > #{startDate}


        and a.endDate < #{endDate}
